import io.vavr.collection.List;
import io.vavr.collection.Stream;

public class Day6TuningTrouble {
    public static void main(String[] args) {
        Day6TuningTrouble day6 = new Day6TuningTrouble();
        day6.firstMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb");
        System.out.println("should be 7");
        day6.firstMarker("bvwbjplbgvbhsrlpgdmjqwftvncz");
        System.out.println("should be 5");
        day6.firstMarker("nppdvjthqldpwncqszvftbrmjlhg");
        System.out.println("should be 6");
        day6.firstMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg");
        System.out.println("should be 10");
        day6.firstMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw");
        System.out.println("should be 11");


        String mainInput="\n";
        day6.firstMarker(mainInput);
    }

    private void firstMarker(String input) {
        int groupSize = 14;
        int markerPosition = List.ofAll(input.toCharArray())
                .sliding(groupSize, 1)
                .zipWithIndex()
                .dropWhile(tup -> tup._1.toSet().size() != tup._1.size())
                .head()._2 + groupSize;

        System.out.println(markerPosition);
    }
}
